function loadChapter(e, c, b) {
    var a = "/chapters/content";
    var d = {
        id: e,
        type: c,
        token: b
    };
    $.ajax({
        type: "POST",
        url: a,
        data: d,
        success: function (f) {
            if (f) {
                $("#bookContentBody").html(f)
            } else {
                $("#bookContentBody").text("Nội dung đang được cập nhật. Vui lòng quay lại sau!").addClass("center")
            }
        },
        error: function (h, f, g) {
            Materialize.toast("Lỗi không xác định. Vui lòng thử lại sau!", 2000)
        }
    })
}
;